INSERT INTO `email_template` VALUES (1,'Submission Received','submission_received','Paper Submission Received - {{paper_title}}','Dear {{author_name}},\n\nThank you for submitting your paper titled \"{{paper_title}}\" to {{journal_name}}.\n\nYour submission has been received and assigned the Paper ID: {{paper_id}}.\n\nWe will review your submission and notify you of our decision. Please allow 2-4 weeks for the initial review process.\n\nIf you have any questions, please contact our editorial office.\n\nBest regards,\nThe Editorial Team\n{{journal_name}}','[\"author_name\", \"paper_title\", \"journal_name\", \"paper_id\"]','submission',1,'2026-02-22 01:30:56','2026-02-22 01:30:56'),(2,'Under Review','under_review','Your Paper is Under Review - {{paper_title}}','Dear {{author_name}},\n\nWe are pleased to inform you that your paper titled \"{{paper_title}}\" (Paper ID: {{paper_id}}) has been sent for peer review.\n\nThe review process typically takes 4-8 weeks. We will notify you once we receive the reviewers\' feedback.\n\nThank you for your patience.\n\nBest regards,\nThe Editorial Team\n{{journal_name}}','[\"author_name\", \"paper_title\", \"journal_name\", \"paper_id\"]','review',1,'2026-02-22 01:30:56','2026-02-22 01:30:56'),(3,'Revision Requested','revision_requested','Revision Requested for Your Paper - {{paper_title}}','Dear {{author_name}},\n\nThank you for submitting your paper titled \"{{paper_title}}\" (Paper ID: {{paper_id}}) to {{journal_name}}.\n\nAfter careful review, the reviewers have requested revisions to your manuscript. Please find the reviewer comments below:\n\n{{reviewer_comments}}\n\nPlease submit your revised manuscript within 30 days. When resubmitting, please include a detailed response to each reviewer comment.\n\nIf you have any questions, please don\'t hesitate to contact us.\n\nBest regards,\nThe Editorial Team\n{{journal_name}}','[\"author_name\", \"paper_title\", \"journal_name\", \"paper_id\", \"reviewer_comments\"]','decision',1,'2026-02-22 01:30:56','2026-02-22 01:30:56'),(4,'Paper Accepted','paper_accepted','Congratulations! Your Paper Has Been Accepted - {{paper_title}}','Dear {{author_name}},\n\nWe are pleased to inform you that your paper titled \"{{paper_title}}\" (Paper ID: {{paper_id}}) has been accepted for publication in {{journal_name}}.\n\nCongratulations on this achievement!\n\nOur production team will contact you shortly with information about the publication process, including proofreading and formatting requirements.\n\nThank you for choosing {{journal_name}} for your research.\n\nBest regards,\nThe Editorial Team\n{{journal_name}}','[\"author_name\", \"paper_title\", \"journal_name\", \"paper_id\"]','decision',1,'2026-02-22 01:30:56','2026-02-22 01:30:56'),(5,'Paper Rejected','paper_rejected','Decision on Your Paper Submission - {{paper_title}}','Dear {{author_name}},\n\nThank you for submitting your paper titled \"{{paper_title}}\" (Paper ID: {{paper_id}}) to {{journal_name}}.\n\nAfter careful consideration by our reviewers and editorial board, we regret to inform you that your paper has not been accepted for publication.\n\n{{rejection_reason}}\n\nWe encourage you to consider the feedback provided and submit future research to our journal.\n\nThank you for your interest in {{journal_name}}.\n\nBest regards,\nThe Editorial Team\n{{journal_name}}','[\"author_name\", \"paper_title\", \"journal_name\", \"paper_id\", \"rejection_reason\"]','decision',1,'2026-02-22 01:30:56','2026-02-22 01:30:56'),(6,'General Inquiry','general_inquiry','RE: {{paper_title}} (Paper ID: {{paper_id}})','Dear {{author_name}},\n\nThank you for your inquiry regarding your paper titled \"{{paper_title}}\" (Paper ID: {{paper_id}}).\n\n{{custom_message}}\n\nIf you have any further questions, please don\'t hesitate to contact us.\n\nBest regards,\n{{sender_name}}\n{{journal_name}}','[\"author_name\", \"paper_title\", \"journal_name\", \"paper_id\", \"custom_message\", \"sender_name\"]','general',1,'2026-02-22 01:30:56','2026-02-22 01:30:56'),(7,'Reviewer Invitation','reviewer_invitation','Invitation to Review - {{paper_title}}','Dear {{reviewer_name}},\n\nWe would like to invite you to review a paper titled \"{{paper_title}}\" for {{journal_name}}.\n\nAbstract:\n{{paper_abstract}}\n\nIf you are able to review this paper, please respond within 7 days. The review deadline would be {{review_deadline}}.\n\nTo accept or decline this invitation, please click the link below:\n{{invitation_link}}\n\nThank you for your valuable contribution to the peer review process.\n\nBest regards,\nThe Editorial Team\n{{journal_name}}','[\"reviewer_name\", \"paper_title\", \"journal_name\", \"paper_abstract\", \"review_deadline\", \"invitation_link\"]','review',1,'2026-02-22 01:30:56','2026-02-22 01:30:56'),(8,'Status Update','status_update','Status Update - {{paper_title}}','Dear {{author_name}},\n\nThis is an update regarding your paper titled \"{{paper_title}}\" (Paper ID: {{paper_id}}).\n\n{{status_message}}\n\nCurrent Status: {{current_status}}\n\nIf you have any questions, please contact our editorial office.\n\nBest regards,\nThe Editorial Team\n{{journal_name}}','[\"author_name\", \"paper_title\", \"journal_name\", \"paper_id\", \"status_message\", \"current_status\"]','general',1,'2026-02-22 01:30:56','2026-02-22 01:30:56');
